require 'set'

molecule = ""

lines = IO.readlines("input").map{|l| l.chomp.split(" => ")}

moles = Set.new

lines.each do |(from, to)|
  molecule.scan(/#{from}/) do |c|
    mole = molecule[0..Regexp.last_match.offset(0).first-1] + to + molecule[Regexp.last_match.offset(0).first+from.length..-1]
    moles.add(mole)
  end
end

puts moles.count

new_mole = "e"

